SELECT * FROM cast_info AS ci, aka_name AS an, movie_companies AS mc WHERE ci.note = '(voice)' AND mc.note LIKE '%(200%)%' AND (mc.note LIKE '%(USA)%' OR mc.note LIKE '%(worldwide)%') AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;