SELECT * FROM company_name AS cn, movie_companies AS mc, cast_info AS ci, role_type AS rt, char_name AS chn WHERE ci.note = '(voice)' AND cn.country_code = '[us]' AND mc.note LIKE '%(200%)%' AND (mc.note LIKE '%(USA)%' OR mc.note LIKE '%(worldwide)%') AND rt.role = 'actress' AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND mc.company_id = cn.id AND cn.id = mc.company_id AND ci.role_id = rt.id AND rt.id = ci.role_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id;