SELECT * FROM cast_info AS ci, char_name AS chn, role_type AS rt, title AS t WHERE ci.note = '(voice)' AND rt.role = 'actress' AND t.production_year BETWEEN 2007 AND 2010 AND ci.movie_id = t.id AND t.id = ci.movie_id AND ci.role_id = rt.id AND rt.id = ci.role_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id;