SELECT * FROM cast_info AS ci, aka_name AS an, movie_companies AS mc WHERE ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;